function.execute:
try{
 var oneJSON= y.xmlToJson(oneXML);
	var oneJSONLD={};
	oneJSONLD['@id']=URI;
	oneJSONLD['@context']= {"schema":"http://schema.org/","schema:itemListElement":{"@type":"@id"}};
	oneJSONLD['@type']= 'schema:ItemList';

  oneJSONLD['schema:numberOfItems'] = getValue("oneJSON['list']['count']");
  oneJSONLD['schema:title'] = getValue("oneJSON['list']['name']");
  oneJSONLD['schema:description'] = getValue("oneJSON['list']['description']");
    oneJSONLD['schema:itemListElement']=[];
  for (var LOOP4 = 0; LOOP4 < getLength(oneJSON['list']['entry']); LOOP4++){
    oneJSONLD['schema:itemListElement'].push (getInterlink('http://rdf.onekin.org/blabla/vtodo/{$ID}' ,getValue("oneJSON['list']['entry'][LOOP4]['id']")));
  }
     }catch (err){ y.log(err);}
    response.object = oneJSONLD;

function getInterlink (urlpattern, value){
	try{return value? urlpattern.replace(/{.*}/, value) : null;}catch(err){return null;}}

function getRegexpValue(regexp, value){
 	try{return value.match(regexp)[0];}catch(err){return null;}}

function getValue(dataPath) {
	try{return eval(dataPath) || null; }catch(err){return null;}}

function getLength(obj) {
  	try{return obj? obj.length: 0; }catch(err){return 0;}}
