select.execute:

 var loweringselect = "env 'store://datatables.org/alltableswithkeys'; select * from flickr.photos.info where photo_id=@photo_id and api_key=@api_key";
 var loweringparams ={};
 loweringparams ['photo_id']= photo_id;
loweringparams ['api_key']= api_key;

 var loweringquery = y.query (loweringselect,loweringparams); 
 response.object =  loweringquery.results;

function.execute:
try{
 var oneJSON= y.xmlToJson(oneXML);
	var oneJSONLD={};
	oneJSONLD['@id']=URI;
	oneJSONLD['@context']= {"schema":"http://schema.org/","schema:about":{"@type":"@id"},"schema:currenciesAccepted":{"@type":"@id"}};
	oneJSONLD['@type']= 'schema:APIReference';

  oneJSONLD['schema:awayTeam'] = getValue("oneJSON['photo']['dateuploaded']");
  oneJSONLD['schema:currenciesAccepted'] = getInterlink('http://dbpedia.org/resource/{$VALUE}', getRegexpValue (/[^\s]+/,getValue("oneJSON['photo']['owner']['realname']")));
  oneJSONLD['schema:currenciesAccepted2'] = getInterlink('http://dbpedia.org/resource/{$VALUE}', getRegexpValue (/[^\s]+/,getValue("oneJSON['photo']['owner']['realname']")));
    oneJSONLD['schema:about']=[];
    oneJSONLD['schema:yearlyRevenue']=[];
  for (var LOOP5 = 0; LOOP5 < getLength(oneJSON['photo']['tags']['tag']); LOOP5++){
    oneJSONLD['schema:yearlyRevenue'].push (getRegexpValue(/[^-]+/, getValue("oneJSON['photo']['tags']['tag'][LOOP5]['id']")));
    oneJSONLD['schema:about'].push (getInterlink('http://dbpedia.org/resource/{$VALUE}' ,getValue("oneJSON['photo']['tags']['tag'][LOOP5]['content']")));
  }
  oneJSONLD['schema:encodings'] = getValue("oneJSON['photo']['urls']['url']['content']");
     }catch (err){ y.log(err);}
    response.object = oneJSONLD;

function getInterlink (urlpattern, value){
	try{return value? urlpattern.replace(/{.*}/, value) : null;}catch(err){return null;}}

function getRegexpValue(regexp, value){
 	try{return value.match(regexp)[0];}catch(err){return null;}}

function getValue(dataPath) {
	try{return eval(dataPath) || null; }catch(err){return null;}}

function getLength(obj) {
  	try{return obj? obj.length: 0; }catch(err){return 0;}}
