function.execute:
try{
 var oneJSON= y.xmlToJson(oneXML);
	var oneJSONLD={};
	oneJSONLD['@id']=URI;
	oneJSONLD['@context']= {"schema":"http://schema.org/","schema:additionalType":{"@type":"@id"},"schema:about":{"@type":"@id"}};
	oneJSONLD['@type']= 'schema:VideoObject';

  oneJSONLD['schema:additionalType'] = getInterlink('http://vivoweb.org/ontology/core#{type}' ,getValue("oneJSON['photo']['media']"));
  oneJSONLD['schema:interactionCount'] = getValue("oneJSON['photo']['views']");
  oneJSONLD['schema:name'] = getValue("oneJSON['photo']['title']");
  oneJSONLD['schema:description'] = getValue("oneJSON['photo']['description']");
  oneJSONLD['schema:uploadDate'] = getValue("oneJSON['photo']['dates']['taken']");
    oneJSONLD['schema:about']=[];
  for (var LOOP5 = 0; LOOP5 < getLength(oneJSON['photo']['tags']['tag']); LOOP5++){
    oneJSONLD['schema:about'].push (getInterlink('http://dbpedia.org/resource/{$VALUE}' ,getValue("oneJSON['photo']['tags']['tag'][LOOP5]['raw']")));
  }
  oneJSONLD['schema:contentUrl'] = getValue("oneJSON['photo']['urls']['url']['content']");
  oneJSONLD['schema:duration'] = getValue("oneJSON['photo']['video']['duration']");
     }catch (err){ y.log(err);}
    response.object = oneJSONLD;

function getInterlink (urlpattern, value){
	try{return value? urlpattern.replace(/{.*}/, value) : null;}catch(err){return null;}}

function getRegexpValue(regexp, value){
 	try{return value.match(regexp)[0];}catch(err){return null;}}

function getValue(dataPath) {
	try{return eval(dataPath) || null; }catch(err){return null;}}

function getLength(obj) {
  	try{return obj? obj.length: 0; }catch(err){return 0;}}
