select.execute:

 var loweringselect = "env 'store://datatables.org/alltableswithkeys'; select * from local.search(1) where zip=@zip and query=@query";
 var loweringparams ={};
 loweringparams ['zip']= zip;
loweringparams ['query']= query;

 var loweringquery = y.query (loweringselect,loweringparams); 
 response.object =  loweringquery.results;

function.execute:
try{
 var oneJSON= y.xmlToJson(oneXML);
	var oneJSONLD={};
	oneJSONLD['@id']=URI;
	oneJSONLD['@context']= {"schema":"http://schema.org/","schema:acceptedPaymentMethod":{"@type":"@id"},"schema:about":{"@type":"@id"}};
	oneJSONLD['@type']= 'schema:APIReference';

  oneJSONLD['schema:action'] = getValue("oneJSON['Result']['id']");
  oneJSONLD['schema:acceptedAnswer'] = getRegexpValue(/[^:]+:[^:]+/, getValue("oneJSON['Result']['xmlns']"));
  oneJSONLD['schema:acceptedPaymentMethod'] = getInterlink('http://dbpedia.org/resource/{$VALUE}' ,getValue("oneJSON['Result']['City']"));
  oneJSONLD['schema:about'] = getInterlink('http://dbpedia.org/resource/{$VALUE}', getRegexpValue (/./,getValue("oneJSON['Result']['State']")));
     }catch (err){ y.log(err);}
    response.object = oneJSONLD;

function getInterlink (urlpattern, value){
	try{return value? urlpattern.replace(/{.*}/, value) : null;}catch(err){return null;}}

function getRegexpValue(regexp, value){
 	try{return value.match(regexp)[0];}catch(err){return null;}}

function getValue(dataPath) {
	try{return eval(dataPath) || null; }catch(err){return null;}}

function getLength(obj) {
  	try{return obj? obj.length: 0; }catch(err){return 0;}}
