function.execute:
try{
 var oneJSON= y.xmlToJson(oneXML);
	var oneJSONLD={};
	oneJSONLD['@id']=URI;
	oneJSONLD['@context']= {"dcterms":"http://purl.org/dc/terms/","schema":"http://schema.org/","schema:containedIn":{"@type":"@id"},"geo":"http://www.w3.org/2003/01/geo/wgs84_pos"};
	oneJSONLD['@type']= 'geo:SpatialThing';

  oneJSONLD['dcterms:identifier'] = getValue("oneJSON['place']['name']");
  oneJSONLD['schema:containedIn'] = getInterlink('http://rdf.onekin.org/geo/spatialthing/{dcterms:identifier}' ,getValue("oneJSON['place']['admin2']['content']"));
  var oneJSONLD3={};
  oneJSONLD3['@type']= 'geo:Point';
  oneJSONLD3['@id'] = URI+'#geo:Point';
  oneJSONLD3['geo:lat'] = getValue("oneJSON['place']['centroid']['latitude']");
  oneJSONLD3['geo:long'] = getValue("oneJSON['place']['centroid']['longitude']");
  oneJSONLD['geo:location'] = oneJSONLD3;
  oneJSONLD['geo:lat'] = getValue("oneJSON['place']['boundingBox']['southWest']['latitude']");
  oneJSONLD['geo:long'] = getValue("oneJSON['place']['boundingBox']['southWest']['longitude']");
     }catch (err){ y.log(err);}
    response.object = oneJSONLD;

function getInterlink (urlpattern, value){
	try{return value? urlpattern.replace(/{.*}/, value) : null;}catch(err){return null;}}

function getRegexpValue(regexp, value){
 	try{return value.match(regexp)[0];}catch(err){return null;}}

function getValue(dataPath) {
	try{return eval(dataPath) || null; }catch(err){return null;}}

function getLength(obj) {
  	try{return obj? obj.length: 0; }catch(err){return 0;}}
