select.execute:

var loweringselect = "env 'store://datatables.org/alltableswithkeys'; use 'https://raw.githubusercontent.com/yql/yql-tables/master/vimeo/vimeo.video.xml' as vimeo.video; select * from vimeo.video where video_id=@video_id";
var loweringparams ={};
loweringparams ['video_id']= video_id;

var loweringquery = y.query (loweringselect,loweringparams); 
response.object =  loweringquery.results;

function.execute:
try{
 var oneJSON= y.xmlToJson(oneXML);
	var oneJSONLD={};
	oneJSONLD['@id']=URI;
	oneJSONLD['@context']= {"schema":"http://schema.org/","schema:about":{"@type":"@id"},"schema:acceptedAnswer":{"@type":"@id"}};
	oneJSONLD['@type']= 'schema:Abdomen';
  oneJSONLD['schema:about'] = getInterlink('http://dbpedia.org/resource/{$VALUE}' ,getValue("oneJSON['videos']['video']['id']"));
  oneJSONLD['schema:actor'] = getValue("oneJSON['videos']['video']['title']");
  oneJSONLD['schema:actionStatus'] = getRegexpValue(/........../, getValue("oneJSON['videos']['video']['description']"));
  oneJSONLD['schema:acceptedAnswer'] = getInterlink('http://dbpedia.org/resource/{$VALUE}/checking', getRegexpValue (/.../,getValue("oneJSON['videos']['video']['user_id']")));
  oneJSONLD['schema:acceptedAnswer1'] = getInterlink('http://dbpedia.org/resource/{$VALUE}/checking', getRegexpValue (/.../,getValue("oneJSON['videos']['video']['user_id']")));
     }catch (err){ y.log(err);}
    response.object = oneJSONLD;

function getInterlink (urlpattern, value){
	try{return value? urlpattern.replace(/{.*}/, value) : null;}catch(err){return null;}}

function getRegexpValue(regexp, value){
 	try{return value.match(regexp)[0];}catch(err){return null;}}

function getValue(dataPath) {
	try{return eval(dataPath) || null; }catch(err){return null;}}

function getLength(obj) {
  	try{return obj? obj.length: 0; }catch(err){return 0;}}
