function.execute:
try{
 var oneJSON= y.xmlToJson(oneXML);
	var oneJSONLD={};
	oneJSONLD['@id']=URI;
	oneJSONLD['@context']= {"foaf":"http://xmlns.com/foaf/0.1/","bibo":"http://purl.org/ontology/bibo/","bibo:abstract":{"@type":"@id"},"adms":"http://www.w3.org/ns/adms","adms:identifier":{"@type":"@id"}};
	oneJSONLD['@type']= 'adms:Asset';

  oneJSONLD['foaf:account'] = getValue("oneJSON['videos']['video']['id']");
  oneJSONLD['foaf:accountName'] = getRegexpValue(/[^ ]*/, getValue("oneJSON['videos']['video']['title']"));
  oneJSONLD['bibo:abstract'] = getInterlink('http://dbpedia.org/resource/{$VALUE}', getRegexpValue (/[^,]+/,getValue("oneJSON['videos']['video']['tags']")));
  oneJSONLD['adms:identifier'] = getInterlink('http://dbpedia.org/resource/{$VALUE}' ,getValue("oneJSON['videos']['video']['embed_privacy']"));
     }catch (err){ y.log(err);}
    response.object = oneJSONLD;

function getInterlink (urlpattern, value){
	try{return value? urlpattern.replace(/{.*}/, value) : null;}catch(err){return null;}}

function getRegexpValue(regexp, value){
 	try{return value.match(regexp)[0];}catch(err){return null;}}

function getValue(dataPath) {
	try{return eval(dataPath) || null; }catch(err){return null;}}

function getLength(obj) {
  	try{return obj? obj.length: 0; }catch(err){return 0;}}
