select.execute:

default xml namespace ='http://where.yahooapis.com/v1/schema.rng'; 
var x = y.query('select woeid from geo.places(1) where text="'+location+'"');
var s = x.results;
var woeid = s..woeid;
var weather = y.rest('http://weather.yahooapis.com/forecastrss?w='+woeid+'&u='+unit).get().response;
response.object = <weather>{weather}</weather>;

function.execute:

var oneJSON= y.xmlToJson(oneReportXML);
var oneJSONLD={'@context':{rdfs:'http://www.w3.org/2000/01/rdf-schema#',
geo:'http://www.w3.org/2003/01/geo/wgs84_pos#', 
wth:'http://www.scs.ryerson.ca/~bgajdero/msc_thesis/code/ontologies/weather-ont-t2.owl'}};
oneJSONLD['@id']= URI;
oneJSONLD['@type']='wth:WeatherReport';
oneJSONLD['geo:location']='http://www.dbpedia.org/resource/'+oneJSON.weather.rss.channel.location.city;
oneJSONLD['geo:lat']=oneJSON.weather.rss.channel.item.lat;
oneJSONLD['geo:long']=oneJSON.weather.rss.channel.item.long;
oneJSONLD['wth:hasReportTime']= oneJSON.weather.rss.channel.item.condition.date;
oneJSONLD['wth:windSpeed']= oneJSON.weather.rss.channel.wind.speed;
oneJSONLD['wth:windDirectionDegrees']= oneJSON.weather.rss.channel.wind.direction;
oneJSONLD['wth:humidityPercent']= oneJSON.weather.rss.channel.atmosphere.humidity;
oneJSONLD['wth:pressureMeasurement']= oneJSON.weather.rss.channel.atmosphere.pressure;
oneJSONLD['wth:visibilityDistance']= oneJSON.weather.rss.channel.atmosphere.visibility;
oneJSONLD['wth:celsiusTemperature']= oneJSON.weather.rss.channel.item.condition.temp;
response.object = oneJSONLD;
