insert.execute:
var loweringinsert = "env 'store://datatables.org/alltableswithkeys'; insert into wordpress.post (blogurl, username, password, title, description, tags) values (@blogurl, @username, @password, @title, @description,  @tags)";
var loweringparams ={}; 
loweringparams ['blogurl']= "https://"+inputs['sioc:id']+".wordpress.com";
loweringparams ['username']= inputs['foaf:acccountName'];
loweringparams ['password']= inputs['acc:password'];
loweringparams ['title']= inputs['dcterms:title'];
loweringparams ['description']= inputs['sioc:content'];
loweringparams ['tags']= inputs['sioc:topic'];
var loweringquery = y.query (loweringinsert,loweringparams);

select.execute:

var loweringselect = "env 'store://datatables.org/alltableswithkeys'; select * from feednormalizer where url=@blogfeed and output='atom_1.0'";
var loweringparams ={};
